import ItemsJSON from '../assets/jsons/items.json';
import NexomonJSON from '../assets/jsons/nexomon-extinction.json';
import StatusJSON from '../assets/jsons/status-effects.json';
import TypeJSON from '../assets/jsons/type-effectiveness.json';
import VaultJSON from '../assets/jsons/vaults-and-keys.json';
import { useHistory } from 'react-router-dom';


export const server_calls = {
    items: async () => {
        const response = await fetch('http://128.199.5.111/api/item/all');
        let data = await response;
        console.log(data);
        return data;
    },

    nexomon: async () => {
        const response = await fetch('http://128.199.5.111/api/nexomon/all');
        let data = await response;
        console.log(data);
        return data;
    },

    status: () => {
        let result = StatusJSON;
        return result;
    },

    type: () => {
        let result = TypeJSON;
        return result;
    },

    vaults: () => {
        let result = VaultJSON;
        return result;
    },

    signin: async (email, password) => {
        const response = await fetch('https://ct-nexopedia-adamschellenberg.herokuapp.com/signin', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                email: email,
                password: password
            })
        });

        if (!response.ok) {
            throw new Error ('Failed to login')
        };
        let data = await response.json()
        console.log ('Token: ', data.token);
        return alert('Successfully signed in!');
    },

    signup: async (email, password) => {
        const response = await fetch('https://ct-nexopedia-adamschellenberg.herokuapp.com/signup', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                email: email,
                password: password
            })
        });

        if (!response.ok) {
            throw new Error ('Failed to create new account')
        };
        server_calls.signin(email, password);
        let data = await response.json()
        console.log('Response: ', data.success);

        return alert('Successfully created an account!');
    },

    logout: () => {

    }
}
